                q.add(new pair(p.ii+1,p.jj,dp[p.ii+1][p.jj]));
                visited[p.ii+1][p.jj]=true;
            }
            if(valid(p.ii,p.jj-1,m,n,visited)){
                dp[p.ii][p.jj-1]=grid[p.ii][p.jj-1]+dp[p.ii][p.jj];
                q.add(new pair(p.ii,p.jj-1,dp[p.ii][p.jj-1]));
                visited[p.ii][p.jj-1]=true;
            }
            if(valid(p.ii,p.jj+1,m,n,visited)){
                dp[p.ii][p.jj+1]=grid[p.ii][p.jj+1]+dp[p.ii][p.jj];
                q.add(new pair(p.ii,p.jj+1,dp[p.ii][p.jj+1]));
                visited[p.ii][p.jj+1]=true;
            }
        }
        return dp[m-1][n-1];

    }
}
