So, this is my first post around here. For this solution, I took inspiration from **"votrubac's"** explanation. 
The code is as follows:

```
class Solution {
public:

    bool isValid(int i,int j,int n,int m)
    {
        if(i<0||j<0) return false;
        if(i>=n||j>=m) return false;
        return true;
    }

    int dx[4]={0,1,0,-1};
    int dy[4]={-1,0,1,0};

    // function
    int minimumObstacles(vector<vector<int>>& grid) {

        int n=grid.size(),m=grid[0].size();
        vector<vector<int>> vis(n, vector<int> (m,-1));
        int wall=0;                         // minesweeper wall
        queue<pair<int,int>> obs,trs;       // obs == obstacle queue  &   trs == traversal queue


        // bfs starts

        vis[0][0]=wall;
        //q.push({0,0});
        trs.push({0,0});
        while(!trs.empty())
        {
            int a=trs.size();
            for(int i=0;i<a;i++)
            {
				// visiting all the 4 adjacent cells
                for(int j=0;j<4;j++)
                {
                    if(isValid(trs.front().first+dy[j],trs.front().second+dx[j],n,m))
                    {
                        if(grid[trs.front().first+dy[j]][trs.front().second+dx[j]]==1&& vis[trs.front().first+dy[j]][trs.front().second+dx[j]]==-1)
                        {
                            vis[trs.front().first+dy[j]][trs.front().second+dx[j]]=wall+1;
                            obs.push({trs.front().first+dy[j],trs.front().second+dx[j]});
                        }
                        else if(grid[trs.front().first+dy[j]][trs.front().second+dx[j]]==0&& vis[trs.front().first+dy[j]][trs.front().second+dx[j]]==-1)
                        {
                            vis[trs.front().first+dy[j]][trs.front().second+dx[j]]=wall;
                            trs.push({trs.front().first+dy[j],trs.front().second+dx[j]});
                        }
                    }
                }
                trs.pop();
            }
            if(vis[n-1][m-1]!=-1)
            {
                return vis[n-1][m-1];
            }


            // swapping trs and obs when trs is empty but obs is not to simulate that a wall has been reached 
            // ans we need to break it to get past

            if(trs.empty())
            {
                while(!obs.empty())
                {
                    trs.push(obs.front());
                    obs.pop();
                }
                wall++;
            }
        }

        // bfs ends here

        return vis[n-1][m-1];
    }
};
```

