class Solution {
public:
    vector<int> dx = {0, 0, 1, -1};
    vector<int> dy = {1, -1, 0, 0};
    int minimumObstacles(vector<vector<int>>& grid) {
        int row = grid.size();
        int col = grid[0].size();
        vector<vector<int>> res(row, vector<int>(col, INT_MAX));
        res[0][0] = 0;
        priority_queue<tuple<int, int, int>, vector<tuple<int, int, int>>, std::greater<tuple<int, int, int>>> pq;
        pq.push({grid[0][0], 0, 0});

        while (!pq.empty()) {
            auto [current, x, y] = pq.top();
            pq.pop();

            if (x == row - 1 && y == col - 1) {
                return current + grid[x][y]; // reached destination
            }

            for (int i = 0; i < 4; i++) {
                auto xx = x + dx[i];
                auto yy = y + dy[i];

                if (xx < 0 || yy < 0 || xx >= row || yy >= col) continue;

                if (res[xx][yy] > current + grid[xx][yy]) {
                    res[xx][yy] = current+ grid[xx][yy]; // new path more optimal
                    pq.push({current + grid[xx][yy], xx, yy});
                }
            }
        }

        return res[row - 1][col - 1];
    }
};
