class Solution {
public:
    int minimumObstacles(vector<vector<int>>& v) {
        int n=v.size();
        int m=v[0].size();
        set<pair<int,pair<int,int>> >st;
        st.insert({v[0][0],{0,0}});

        int dx[]={-1,1,0,0};
        int dy[]={0,0,-1,1};

        int dis[n][m];
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                dis[i][j]=1e9;
            }
        }


        bool vis[n][m];
        memset(vis,false,sizeof(vis));
        while(!st.empty()){
            int x,y,w;
            auto it=st.begin();
            x=it->second.first;
            y=it->second.second;
            w=it->first;
            st.erase(st.begin());
            if(dis[x][y]<w) continue;
            if(vis[x][y]) continue;
            dis[x][y]=min(dis[x][y],w);
            if(x==n-1 and y==m-1) return w; 
            vis[x][y]=true;
            for(int i=0;i<4;i++){
                int nx,ny;
                nx=x+dx[i];
                ny=y+dy[i];
                if(nx>=0 and ny>=0 and nx<n and ny<m){
                    if(dis[nx][ny]>w+v[nx][ny])
                    {st.insert({w+v[nx][ny],{nx,ny}}); dis[nx][ny]=w+v[nx][ny]; }
                }
            }

        }

        return dis[n-1][m-1];


    }
};

