//dijkstra according to no of obstacles
//make dist[] array which contains no of obstacles till each coordinate in the path

class Solution {
    class tuple{
        int a,b,c;
        tuple(int a,int b,int c){
            this.a=a;
            this.b=b;
            this.c=c;
        }
    }
    public int minimumObstacles(int[][] grid) {
        int m=grid.length,n=grid[0].length;
        int dist[][]=new int[m][n];
        for(int i[]:dist){
            Arrays.fill(i,1000000);
        }
        dist[0][0]=0;
        //(number of Obstacles removed till then,x,y)
        PriorityQueue<tuple>q=new PriorityQueue<>((z,v)->z.a-v.a);
        q.offer(new tuple(0,0,0));
        int rx[]={-1,1,0,0};
        int ry[]={0,0,-1,1};
        while(!q.isEmpty()){
            tuple t=q.poll();
            for(int i=0;i<4;i++)
            {
                int x=rx[i]+t.b;
                int y=ry[i]+t.c;
                if(x>=0&&y>=0&&x<m&&y<n){
                    if(grid[x][y]==0){
                        if(dist[x][y]>t.a){
                        dist[x][y]=t.a;
                        q.offer(new tuple(dist[x][y],x,y));
                        }
                    }
                    else{
                        if(dist[x][y]>t.a+1){
                            dist[x][y]=t.a+1;
                            q.offer(new tuple(dist[x][y],x,y));
                        }
                    }
                }
            }
        }
        return dist[m-1][n-1];
    }
}
