class Solution {
    int min_removal= Integer.MAX_VALUE;
    public int minimumObstacles(int[][] grid) {
        boolean[][] visited = new boolean[grid.length][grid[0].length];
        dfs(0,0,grid,0,visited);
        return min_removal;
    }
    public void  dfs(int i,int j, int[][] grid,int count,boolean[][] visited)

        // if we are on the last index then we will return min removal

        if(i<0 ||  j<0 || i>=grid.length || j>=grid[0].length || visited[i][j]==true){
            return;
        }
        if(i==grid.length-1 && j==grid[0].length-1){
            min_removal = Math.min(min_removal,count);
            return;
        }
        visited[i][j]= true;
        //we can remove as much obstcale we can but we need to return minimum;
        if(grid[i][j]==1){
            count++;
        }
        //dfs
        dfs(i+1,j,grid,count,visited);
        dfs(i-1,j,grid,count,visited);
        dfs(i,j+1,grid,count,visited);
        dfs(i,j-1,grid,count,visited);
        visited[i][j] = false;

    }
}




