            if(isValid(i-1,j,grid,visited)){
                visited[i-1][j]=true;
                if(grid[i-1][j]==1){
                    pq.add(new pair(i-1,j,step+1));
                }
                else{
                    pq.add(new pair(i-1,j,step));
                }
