            if(isValid(i,j+1,grid,visited)){
                visited[i][j+1]=true;
                if(grid[i][j+1]==1){
                    pq.add(new pair(i,j+1,step+1));
                }
                else{
                    pq.add(new pair(i,j+1,step));
                }
            }
            if(isValid(i,j-1,grid,visited)){
                visited[i][j-1]=true;
                if(grid[i][j-1]==1){
                    pq.add(new pair(i,j-1,step+1));
                }
