        const int row = grid.size(), col = grid[0].size(), oo = INT_MAX;
        using T = tuple<int, int, int>;

        vector<pair<int, int> > directions = { {0,1}, {0,-1}, {1,0}, {-1,0} };
        vector<vector<int>>distance(row, vector<int>(col, oo));
        priority_queue<T, vector<T>, greater<T>> pq;

        pq.push({0,0,0});
        distance[0][0] = 0;

        while(!pq.empty()){

            auto [dist, r, c] = pq.top();  pq.pop();

            if(row-1 ==r and col-1 == c)return dist;

            if(distance[r][c] != dist) continue;

            for(auto& dir : directions){
                  int x = dir.first + r, y = dir.second + c;
                  if(min(x,y) < 0 or x >=row or y>= col) continue;

                  if(dist + grid[x][y] < distance[x][y]){
                       pq.push({distance[x][y] = dist + grid[x][y], x, y});
                  }
            }
        }

        return distance[row-1][col-1];
    }
};
```

