class Solution {
public:
    int minimumObstacles(vector<vector<int>>& grid) {
        const auto INF = int(1e5) + 5;
        const vector<pair<int, int>> DIR = {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};
        int H = (int) grid.size(), W = (int) grid[0].size();
        // vector<vector<int>> dp(H, vector<int> (W, INF));
        int dp[H][W];

        for (int i = 0; i < H; i++)
            for (int j = 0; j < W; j++)
                dp[i][j] = INF;

        deque<pair<int, int>> dq;
        dp[0][0] = 0;
        dq.push_front(make_pair(0, 0));

        while (!dq.empty()) {
            auto [r, c] = dq.front();
            dq.pop_front();

            if (r == H - 1 && c == W - 1)
                return dp[r][c];

            for (auto [x, y] : DIR) 
                if (r + x >= 0 && c + y >= 0 && r + x < H && c + y < W && grid[r + x][c + y] != 2) {
                    if (!grid[r + x][c + y])
                        dq.push_front({r + x, c + y});
                    else
                        dq.push_back({r + x, c + y});

                    dp[r + x][c + y] = dp[r][c] + grid[r + x][c + y];
                    grid[r + x][c + y] = 2;
                }
        }

        return dp[H - 1][W - 1];
    }
};
