class Solution {
public:
    int minimumObstacles(vector<vector<int>>& grid) {
        int weight,r,c;
        vector<vector<int>>visited(grid.size(),vector<int>(grid[0].size(),INT_MAX));
        priority_queue<pair<int,pair<int,int>>,vector<pair<int,pair<int,int>>>,greater<pair<int,pair<int,int>>>>q;
        q.push(pair<int,pair<int,int>>(0,pair<int,int>(0,0)));
        visited[0][0]=0;
        while(!q.empty())
        {
            weight=q.top().first;
            r=q.top().second.first;
            c=q.top().second.second;
            if(r==grid.size()-1 && c==grid[0].size()-1)return weight;
            q.pop();
            weight+=grid[r][c];
            if(r-1>=0 && visited[r-1][c]>weight)
            {
                q.push(pair<int,pair<int,int>>(weight,pair<int,int>(r-1,c)));
                visited[r-1][c]=weight;
            }
            if(c-1>=0 && visited[r][c-1]>weight)
            {
                q.push(pair<int,pair<int,int>>(weight,pair<int,int>(r,c-1)));
                visited[r][c-1]=weight;
            }
             if(r+1<grid.size() && visited[r+1][c]>weight)
            {
                q.push(pair<int,pair<int,int>>(weight,pair<int,int>(r+1,c)));
                visited[r+1][c]=weight;
            }
            if(c+1<grid[0].size() && visited[r][c+1]>weight)
            {
                q.push(pair<int,pair<int,int>>(weight,pair<int,int>(r,c+1)));
                visited[r][c+1]=weight;
            }

        }
        return 0;
    }
};
