class Solution {
public:

    // Approach : Dijkstra's Algorithm ; Implementation Based On Set: STL
    // Time Complexity : o(mnlog(mn)) ; Space Complexity : O(mn)

    // Steps :

    // 1) Initialise the distances of all grid elements as INT_MAX in dp array
    // 2) Insert source into set where <distance, u, v>
    // 3) While set doesn't become empty do,
    //   i) extract minimum distance from set, let extracted be (i, j)
    //   ii) loop through all the adjacents to (i, j) i.e. check in 4 directions 
    //   iii) if(dp[x][y] > grid[x][y] + weight) 
    //       # here, grid[x][y] is edge's weight from (i,j)-->(x,y) 
    //       # weight is minimum distance till (i,j)
    //       # dist[v] > dist[u] + cost (standard relaxation condition from dijkstra)
    //       # dp[x][y] > weight + grid[x][y]
    //       a) Update distance of dp[x][y] = grid[x][y] + weight
    //       b) If (dp[x][y], x, y) already in set remove them and insert with updated distances
    //       c) If (dp[x][y], x, y) not in set, insert it in set 
    // 4) Finally, return distance of destination that will be minimum i.e dp[n-1][m-1]

    int minimumObstacles(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();

        vector<vector<int>> dp(n, vector<int>(m, INT_MAX));

        set<tuple<int,int,int>> st;

        st.insert({0,0,0});
        dp[0][0] = 0;

        int dx[4] = {0,0,1,-1};
        int dy[4] = {1,-1,0,0};


        while(!st.empty()){
            auto tmp = *(st.begin());
            st.erase(st.begin());

            int weight = get<0>(tmp);

            for(int k = 0 ; k < 4 ; k++){
                int x = dx[k] + get<1>(tmp);
                int y = dy[k] + get<2> (tmp);
                if(x >= 0 && x < n && y >= 0 && y < m){
                    if(dp[x][y] > grid[x][y] + weight){
                        if(dp[x][y] != INT_MAX) st.erase({dp[x][y], x, y});
                        dp[x][y] = grid[x][y] + weight;
                        st.insert({dp[x][y], x, y});
                    } 
                }
            }
        }

        return dp[n-1][m-1];

    }
};
