int minimumObstacles(int** grid, int gridSize, int* gridColSize){
    int p = gridSize*(*gridColSize);
    int** list = malloc(sizeof(int*)*p);
    for (int i = 0 ; i < p ; i++){
        list[i] = malloc(sizeof(int)*2);
    }
    int end = 0;
    int count = 0; 
    int start = 0;
    int now = 2;
    input(grid, gridSize, *gridColSize, list, &count, 0, 0, now);
