class Solution {
public:
    #define vi vector<int>
    #define pii pair<int,int>
    #define inf 1e9+9
    #define f first
    #define s second

    int dx[4] = {-1,1,0,0};
    int dy[4] = {0,0,1,-1};
    vector<vector<pii>> buildGraph(vector<vector<int>>& grid,int n, int m){
        vector<vector<pii>> graph(n*m+1);


        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                int currCell = i*m+j;


                for(int d=0;d<4;d++){//all direction
                    int newR = i+dx[d];
                    int newC = j+dy[d];

                    if(newR>=0 and newR<n and newC >= 0 and newC<m){
                        int newCell = newR*m+newC;
                        graph[currCell].push_back({newCell,grid[newR][newC]});
                    }
                }
            }
        }

        return graph;
    }
    int minimumObstacles(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        vector<vector<pii>> graph = buildGraph(grid,n,m);

        vi dist(n*m,inf);
        deque<int> dq;//curr node and wt

        dq.push_back(0);
        dist[0]=0;

        while(!dq.empty()){

            auto curr = dq.front();
            dq.pop_front();

            for(auto child: graph[curr]){
                int child_v = child.f;
                int wt = child.s;

                if(dist[curr]+wt < dist[child_v]){
                    dist[child_v] = dist[curr]+wt;

                    if(wt == 1){
                        dq.push_back(child_v);
                    }
                    else{
                        dq.push_front(child_v);
                    }
                }
            }

        }


        return dist[n*m-1];
    }
};
