class Solution {
public:
    int minimumObstacles(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();

        priority_queue<pair<int, pair<int,int>>, vector<pair<int, pair<int, int>>>, greater<pair<int, pair<int, int>>>> pq;
        vector<vector<int >> distance(n + 1, vector<int>(m + 1, INT_MAX)), vis(n + 1, vector<int>(m + 1, 0));
        pq.push({0, {0, 0}});
        distance[0][0] = 0;

        while(!pq.empty()){
            pair<int, pair<int, int>> currPath = pq.top();
            pq.pop();

            int dis = currPath.first;
            int x = currPath.second.first;
            int y = currPath.second.second;
            if(vis[x][y])
                continue;

            vis[x][y] = 1;
            int dx[] = {-1, 1, 0, 0};
            int dy[] = {0, 0, -1, 1};

            for(int i = 0; i < 4; ++i){
                int newx = dx[i] + x, newy = dy[i] + y;
                if(newx >= 0 and newy >= 0 and newx < n and newy < m){
                    int weight = grid[newx][newy];
                    if(dis + weight < distance[newx][newy]){
                        distance[newx][newy] = dis + weight;

                        pq.push({distance[newx][newy], {newx, newy}});
                    }
                }
            }
        }

        return distance[n - 1][m - 1];
    }
};
