        pq.offer(new int[]{0, 0});

        while(!pq.isEmpty()){

            int[] curr = pq.poll();

            if(curr[0] == m-1 && curr[1] == n-1)
                return dp[m-1][n-1];
            for(int j=0;j<4;j++){
                int x = dir[j][0]+curr[0];
