class Solution:
    def minimumObstacles(self, grid: List[List[int]]) -> int:
        from collections import deque 
        q = deque([(0,0,0)])
        n, m = len(grid), len(grid[0])
        while q:
            i,j,v = q.popleft() 
            if grid[i][j] != -1:
                grid[i][j] = -1
                if i == n-1 and j == m-1:
                    return v
                for (x,y) in (i+1,j),(i,j+1), (i-1, j), (i,j-1):
                    if 0 <= x < n and 0 <= y < m :
                        if grid[x][y] != -1:
                            getattr(q, f'append{"" if grid[x][y] else "left"}' )((x,y,v+grid[x][y]))
