    {
        n = g.size(), m = g[0].size();
        int dp[n][m];
        for (int i = 0; i < n; i++)
            for (int j = 0; j < m; j++)
                dp[i][j] = INT_MAX;
        dp[n - 1][m - 1] = 0;
        int ind = 0;
        vector<vector<int>> v[100005];
        v[0].push_back({n - 1, m - 1});
        while (1)
        {
            if (v[ind].size() == 0)
                ind++;
            if (v[ind].size() == 0)
                break;
            vector<int> x = v[ind].back();
            v[ind].pop_back();
            int i = x[0] - 1, j = x[1];
            if (valid(i, j) && dp[i][j] > dp[x[0]][x[1]] + g[i][j])
            {
                dp[i][j] = dp[x[0]][x[1]] + g[i][j];
                v[dp[i][j]].push_back({i, j});
            }
            i = x[0] + 1, j = x[1];
            if (valid(i, j) && dp[i][j] > dp[x[0]][x[1]] + g[i][j])
            {
                dp[i][j] = dp[x[0]][x[1]] + g[i][j];
                v[dp[i][j]].push_back({i, j});
            }
            i = x[0], j = x[1] - 1;
            if (valid(i, j) && dp[i][j] > dp[x[0]][x[1]] + g[i][j])
            {
                dp[i][j] = dp[x[0]][x[1]] + g[i][j];
                v[dp[i][j]].push_back({i, j});
            }
            i = x[0], j = x[1] + 1;
            if (valid(i, j) && dp[i][j] > dp[x[0]][x[1]] + g[i][j])
            {
                dp[i][j] = dp[x[0]][x[1]] + g[i][j];
                v[dp[i][j]].push_back({i, j});
            }
        }
        return dp[0][0];
    }
};
