class Solution {
public:
    int minimumObstacles(vector<vector<int>>& a) {
        int n = a.size();
        int m = a[0].size();
        deque <array<int, 3>> Q;
        int vis[n][m];
        memset(vis, -1, sizeof vis);
        Q.push_back({0, 0, 0});
        vis[0][0] = 0;
        const int dx[] = {1, -1, 0, 0};
        const int dy[] = {0, 0, 1, -1};
        auto inside = [&](int x, int y) {
          return x >= 0 and x < n and y >=0 and y < m;
        };
        while (!Q.empty()) {
          auto [x, y, d] = Q.front();
          Q.pop_front();
          for (int i = 0; i < 4; ++i) {
            int xx = x + dx[i];
            int yy = y + dy[i];
            if (inside(xx, yy) and vis[xx][yy] == -1) {
              if (a[xx][yy]) {
                vis[xx][yy] = vis[x][y] + 1;
                Q.push_back({xx, yy, 1});
              } else {
                vis[xx][yy] = vis[x][y];
                Q.push_front({xx, yy, 0});
              }
            }
          }
        }
        return vis[n - 1][m - 1];
    }
};
