//use abbreviations when needed
#define pii pair<int,pair<int,int>>

class Solution 
{
public:
    int minimumObstacles(vector<vector<int>>& grid) 
    {
        //directions(top,right,bottom,left)
        //eases the task of traversing adjacent cells
        int dx[4]={0,1,0,-1};
        int dy[4]={-1,0,1,0};

        int m,n;
        m=grid.size();
        n=grid[0].size();

        priority_queue<pii,vector<pii>,greater<pii>> pq;
        vector<vector<int>> dis(m,vector<int>(n,INT_MAX));

        pq.push({0,{0,0}});
        dis[0][0]=0;

        while(!pq.empty())
        {
            //d for distance and c for coordinates
            auto [d,c]=pq.top();
            pq.pop();

            if(c.first==m-1 && c.second==n-1)
            {
                return d;
            }

            for(int i=0;i<4;i++)
            {
                int x=c.first+dx[i];
                int y=c.second+dy[i];

                if(x<0 || x>=m || y<0 || y>=n)
                {
                    continue;
                }

                int currd=d+grid[x][y];

                if(currd<dis[x][y])
                {
                    dis[x][y]=currd;
                    pq.push({currd,{x,y}});
                }
            }
        }

        return -1;
    }
};
