    def minimumObstacles(self, grid: List[List[int]]) -> int:
        dq = collections.deque([(0,0,0)])
        R  = len(grid)
        C  = len(grid[0])
        visited = set([(0,0)])
        dirs = [(1,0),(0,1),(0,-1),(-1,0)]

        while dq:
            r,c,obs = dq.popleft()
            if r == R-1 and c == C-1:
                return obs
            for d in dirs:
                dr,dc = d
                nr,nc = r+dr,c+dc

                if 0<=nr<R and 0<=nc<C and (nr,nc) not in visited:
                    visited.add((nr,nc))
                    if grid[nr][nc] == 1:
                        dq.append((nr,nc,obs+1))
                    else:
                        dq.appendleft((nr,nc,obs))

