class Solution {
public:
    int minimumObstacles(vector<vector<int>>& grid)
    {
        int n=grid.size();
        int m=grid[0].size();
        vector<vector<int>>dp(n,vector<int>(m,INT_MAX));
        priority_queue<pair<int,pair<int,int>>,vector<pair<int,pair<int,int>>>,greater<pair<int,pair<int,int>>>>pq;
        dp[0][0]=0;
        pq.push({0,{0,0}});
        int ar[]={1,-1,0,0};
        int br[]={0,0,1,-1};
        while(!pq.empty())
        {
            int x=pq.top().second.first;
            int y=pq.top().second.second;
            pq.pop();
            if((x==(n-1))&&(y==(m-1)))
            {
                return dp[x][y];
            }
            for(int i=0;i<4;i++)
            {
                int r=x+ar[i];
                int c=y+br[i];
                if((r>=0)&&(c>=0)&&(r<n)&&(c<m))
                {
                    if(grid[r][c]==1)
                    {
                        if(dp[r][c]>1+dp[x][y])
                        {
                            dp[r][c]=1+dp[x][y];
                            pq.push({dp[r][c],{r,c}});
                        }
                    }
                    else
                    {
                        if(dp[r][c]>dp[x][y])
                        {
                            dp[r][c]=dp[x][y];
                            pq.push({dp[r][c],{r,c}});
                        }
                    }
                }
            }
        }
        return dp[n-1][m-1];
    }
};
