# Code
```
class Solution {
public:
    int minimumObstacles(vector<vector<int>>& grid) {
         int n = grid.size();
        int m = grid[0].size();
        vector<vector<int>> cost(n , vector<int> (m , INT_MAX));
        set<pair<int , pair<int , int>>> store;
        cost[0][0] = 0;
        store.insert({0 , {0 , 0}});
        int ROW[] = {0 , 0 , 1 , -1};
        int COL[] = {1 , -1 , 0 , 0};
        while(!store.empty())
        {
            auto begin = *(store.begin());
            int posCost = begin.first;
            int i = begin.second.first;
            int j = begin.second.second;
            store.erase(begin);
            if(i == n-1 && j == m-1) return posCost;
            for(int x = 0 ; x < 4 ; x++)
            {
                int I = i + ROW[x];
                int J = j + COL[x];
                if(I < 0 || J < 0 || I >= n || J >= m || cost[I][J] < posCost + 1) continue;
                if(grid[i][j] == 1)
                {
                    cost[I][J] = posCost + 1;
                    store.insert({posCost+1 , {I , J}});   
                }
                else
                {
                    cost[I][J] = posCost;
                    store.insert({posCost , {I , J}});   
                }
            }
        }
        return -1;
    }
};
```

