class Solution {
    public int minimumObstacles(int[][] grid) {
        return dijkstra(grid);
    }

    // implement using 0 - 1 BFS
    // BFS since we need to calculate MIN obstacles to remove
    // if we encounter a 1 i.e an obstacle put it at the back of the queue
    // we need to process 1 if we run out of cells with 0 and didn't reach 
    // the end (m - 1, n - 1)
    // run BFS by popping from the front i.e preference to zeros
    // when all zeros are exhausted we will encounter a 1 and if we 
    // encounter a zero again we will switch to processing 0 
    // since we append the cell with zero in the front
    // and for each 1 encountered increment the count.

    private int zeroOneBFS(int[][] grid){
        int m = grid.length;
        int n = grid[0].length;
        int dirs[][] = {{1,0},{0,1},{-1,0},{0,-1}};

        boolean[][] visited = new boolean[m][n];

        Deque<int[]> queue = new LinkedList<>();


        queue.offer(new int[]{0, 0, grid[0][0]});
        visited[0][0] = true;

        while (!queue.isEmpty()){
            int[] curr = queue.removeFirst();

            int r = curr[0], c = curr[1], k = curr[2];

            if (r == m - 1 && c == n - 1)
                return k;

            for (int[] d : dirs){
                int nr = r + d[0], nc = c + d[1];
                if (nr >= 0 && nr < m && nc >= 0 && nc < n && !visited[nr][nc]){                    
                    visited[nr][nc] = true;
                    if (grid[nr][nc] == 0){
                        queue.addFirst(new int[]{nr, nc, k});
                    } else {
                        queue.addLast(new int[]{nr, nc, k + 1});
                    }
                }
            }
        }
        return -1;
    }

    //using dijkstra
    // increment the cost ans use it is a priority value 
    // when end is reched return the obstacles removed.

    private int dijkstra(int[][] grid) {

        PriorityQueue<int[]> pq = new PriorityQueue<>((a,b) -> a[2] - b[2]);

        int m = grid.length, n = grid[0].length;

        boolean[][] visited = new boolean[m][n];

        pq.offer(new int[]{0, 0, grid[0][0]});

        visited[0][0] = true;

        int dirs[][] = {{1,0}, {0,1}, {-1,0}, {0,-1}};

        while (!pq.isEmpty()){
            int[] curr = pq.poll();

            int r = curr[0], c = curr[1], k = curr[2];

            if (r == m - 1 && c == n - 1)
                return k;

            for (int[] d : dirs){
                int nr = r + d[0], nc = c + d[1];
                if (nr >= 0 && nr < m && nc >= 0 && nc < n && !visited[nr][nc]){                  visited[nr][nc] = true;
                    if (grid[nr][nc] == 0){
                        pq.offer(new int[]{nr, nc, k});
                    } else {
                        pq.offer(new int[]{nr, nc, k + 1});
                    }
                } 
            }
        }
        return -1;
    }
}
