class Solution {
    public int minimumObstacles(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        int [][]distance = new int[m][n];
        int [][]directions = {{0, 1}, {0, -1}, {1, 0},{-1, 0}};
        for(int []arr : distance) Arrays.fill(arr, Integer.MAX_VALUE);
        Deque<triple> dq = new ArrayDeque<>();
        dq.add(new triple(0, 0, 0));
        distance[0][0] = 0;
        while(!dq.isEmpty()){
            triple curr = dq.remove();
            int x = curr.x;
            int y = curr.y;
            int obs = curr.obs;
            for(int []dir : directions){
                int xx = x + dir[0];
                int yy = y + dir[1];
                if(xx < 0 || xx >= m || yy < 0 || yy >= n){ continue;}
                if(grid[xx][yy] + obs < distance[xx][yy]){
                    distance[xx][yy] = grid[xx][yy] + obs;
                    if(grid[xx][yy] == 0) dq.addFirst(new triple(xx,yy,distance[xx][yy]));
                    else dq.addLast(new triple(xx,yy,distance[xx][yy]));
                }
            }
        }
        return distance[m-1][n-1];
    }
}
class triple {
    int x, y, obs;
    triple(int x, int y, int obs){
        this.x = x;
        this.y = y;
        this.obs = obs;
    }
}
