class Solution:
    def minimumObstacles(self, grid: List[List[int]]) -> int:
        R,C=len(grid),len(grid[0])
        minHeap=[]
        heappush(minHeap,[0,0,0])
        visited=[[0]*C for _ in range(R)]
        while minHeap:
            minObstacles,r,c=heappop(minHeap)
            if r==R-1 and c==C-1:
                return minObstacles
            for newR,newC in [[r+1,c],[r-1,c],[r,c+1],[r,c-1]]:
                if newR in range(R) and newC in range(C) and visited[newR][newC]==0:
                    if grid[newR][newC]:
                        heappush(minHeap,[minObstacles+1,newR,newC])
                    else:
                        heappush(minHeap,[minObstacles,newR,newC])
                    visited[newR][newC]=1
        return -1
