**1. Use the `std::priority_queue`**
```
/**
 * Time Complexity: O(rows * cols * log(rows * cols))
 * Space Complexity: O(rows * cols)
 * where `rows` is the number of the rows of the grid `grid`
 *       `cols` is the number of the columns of the grid `grid`
 */
class Solution {
 public:
  int minimumObstacles(const vector<vector<int>> &grid) {
    using pq_node_t = tuple<int, int, int>;   // {the number of erased obtacles, the row, the column}
    constexpr int directions[] = {0, 1, 0, -1, 0};
    constexpr int n_directions = 4;
    const int rows = static_cast<int>(grid.size());
    const int cols = static_cast<int>(grid.front().size());
    priority_queue<pq_node_t, vector<pq_node_t>, greater<>> pq;
    bool visited[rows][cols];
    memset(visited, 0, sizeof(visited));
    pq.emplace(0, 0, 0);
    while (!pq.empty()) {
      const auto [e, r, c] = pq.top();
      if (r == rows - 1 && c == cols - 1) {
        return e;
      }
      pq.pop();
      if (visited[r][c]) {
        continue;
      }
      visited[r][c] = true;
      for (int d = 0; d < n_directions; ++d) {
        const int nr = r + directions[d];
        const int nc = c + directions[d + 1];
        if (nr >= 0 && nr < rows && nc >= 0 && nc < cols && !visited[nr][nc]) {
          pq.emplace(e + grid[nr][nc], nr, nc);
        }
      }
    }
    throw "impossible path";
  }
};
```
**2. Use the `0-1 BFS`**
```
/**
 * Time Complexity: O(rows * cols)
 * Space Complexity: O(rows * cols)
 * where `rows` is the number of the rows of the grid `grid`
 *       `cols` is the number of the columns of the grid `grid`
 */
class Solution {
 public:
  int minimumObstacles(const vector<vector<int>> &grid) {
    using dq_node_t = tuple<int, int, int>;   // {the number of erased obtacles, the row, the column}
    constexpr int directions[] = {0, 1, 0, -1, 0};
    constexpr int n_directions = 4;
    const int rows = static_cast<int>(grid.size());
    const int cols = static_cast<int>(grid.front().size());
    deque<dq_node_t> dq;
    bool visited[rows][cols];
    memset(visited, 0, sizeof(visited));
    dq.emplace_back(0, 0, 0);
    while (!dq.empty()) {
      const auto [e, r, c] = dq.front();
      if (r == rows - 1 && c == cols - 1) {
        return e;
      }
      dq.pop_front();
      if (visited[r][c]) {
        continue;
      }
      visited[r][c] = true;
      for (int d = 0; d < n_directions; ++d) {
        const int nr = r + directions[d];
        const int nc = c + directions[d + 1];
        if (nr >= 0 && nr < rows && nc >= 0 && nc < cols && !visited[nr][nc]) {
          if (grid[nr][nc] == 0) {
            dq.emplace_front(e, nr, nc);
          } else {
            dq.emplace_back(e + 1, nr, nc);
          }
        }
      }
    }
    throw "impossible path";
  }
};
```

