class Solution {
public:
   int minimumObstacles(vector<vector<int>> &grid)
{
    int n = grid.size();
    int m = grid[0].size();

    int rr[] = {-1, 0, 1, 0};
    int cc[] = {0, 1, 0, -1};

    vector<vector<int>> visited(n, vector<int>(m, 0));
    vector<vector<int>> distance(n, vector<int>(m, 1e9));
    distance[0][0] = 1;

    deque<pair<int, int>> q;
    q.push_back({0, 0});

    while (!q.empty())
    {
        int row = q.front().first;
        int col = q.front().second;
        q.pop_front();

        for (int i = 0; i < 4; i++)
        {
            int r = row + rr[i];
            int c = col + cc[i];

            if (r < n and r >= 0 and c < m and c >= 0 and !visited[r][c])
