                    q.push_back({r, c});
                    distance[r][c] = 1 + distance[row][col];
                }
                else
                {
                    q.push_front({r, c});
                    distance[r][c] = distance[row][col];
                }
            }
        }
    }

    return distance[n - 1][m - 1] - 1;
}

