class Solution {
    int[][] directions = {{1,0},{-1,0},{0,1},{0,-1}};
    public int minimumObstacles(int[][] grid) {

        int endX = grid.length - 1;
        int endY = grid[0].length - 1;

        //0-1 BFS traversal(dequeue rather than priority queue)
        int[][] dist = new int[endX + 1][endY + 1];

        for(int[] arr : dist){
            Arrays.fill(arr,Integer.MAX_VALUE);
        }

        Deque<Pair> dq = new ArrayDeque<Pair>();

        dq.addFirst(new Pair(0,0,0));
        dist[0][0] = 0;

        while(!dq.isEmpty()){

            Pair last = dq.removeFirst();

            int x = last.x;
            int y = last.y;
            int obs = last.obs;

            for(int[] dir : directions){

                int xx = x + dir[0];
                int yy = y + dir[1];

                if(xx < 0 || yy < 0 || xx > endX || yy > endY){
                    continue;
                }

                if(dist[xx][yy] > grid[xx][yy] + obs){

                    dist[xx][yy] = grid[xx][yy] + obs;

                    if(grid[xx][yy] == 0){
                        //add first
                        dq.addFirst(new Pair(xx,yy,dist[xx][yy]));

                    }else if(grid[xx][yy] == 1){
                        //add last
                         dq.addLast(new Pair(xx,yy,dist[xx][yy]));
                    }

                }

            }

        }


        return dist[endX][endY];
    }
}

