class Solution {
public:


    //check for valid index
    bool isValid(int r, int c, const vector<vector<int>>& grid)
    {
        return r >= 0 && c >= 0 
            && r < grid.size() && c < grid.front().size();
    }

    int minimumObstacles(vector<vector<int>>& grid) 
    {
        if(grid.empty())
            return 0;

        int nRows = grid.size();
        int nCols = grid.front().size();

        deque<pair<int,int>> dque;
        //initialize all costs to INT_MAX
        vector<vector<int>> min_cost(nRows, vector<int>(nCols, INT_MAX));

        //set the starting position cost to zero
        min_cost[0][0] = 0;
        dque.push_front({0,0});

        while(!dque.empty())
        {
            int curr_r = dque.front().first;
            int curr_c = dque.front().second;
            int curr_cost = min_cost[curr_r][curr_c];
            dque.pop_front();

            //found target
            if(curr_r== nRows-1 && curr_c == nCols-1)
                return min_cost[curr_r][curr_c];


            array<array<int,2>,4> directions{{{0,1}, {1,0}, {0,-1},{-1,0}}};

            for(auto d : directions)
            {
                int r = curr_r + d[0];
                int c = curr_c + d[1];

                //if invalid cells or if cell already visited
                if(!isValid(r,c,grid) || grid[r][c] == 2)
                    continue;


                // if this neigboring cell is not an obstacle,then its cost
                // is the same as the current cell
                int neighbor_local_cost = grid[r][c] == 0? curr_cost : curr_cost + 1;
                min_cost[r][c] = neighbor_local_cost;

                //mark as visited
                grid[r][c] = 2;


                if(neighbor_local_cost == curr_cost)
                    dque.push_front({r,c});
                else
                    dque.push_back({r,c}); 
            }

        }

        return -1;
    }
};
