class Solution {
public:
    int minimumObstacles(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

         priority_queue<pair<int, pair<int, int>>, vector<pair<int, pair<int, int>>>, greater<pair<int, pair<int, int>>>> pq;

        vector <vector<int>> v(n,vector<int>(m,1e9));

        v[0][0]=grid[0][0];
        //in simple dijkstra we store{dist,node} -> in matrix node is given by {r,c} 
        pq.push({v[0][0],{0,0}}); // {dist,{r,c}}
        int delrow[4] = {-1,0,1,0};
        int delcol[4] = {0,1,0,-1};

        while(!pq.empty()){
            int dist = pq.top().first;
            int r = pq.top().second.first;
            int c = pq.top().second.second;
            pq.pop();

            for(int i=0;i<4;i++){
                int nrow = r+delrow[i];
                int ncol = c+delcol[i];

                if(nrow<n && nrow>=0 && ncol<m && ncol>=0){
                    if(dist+grid[nrow][ncol] < v[nrow][ncol]){
                        v[nrow][ncol] = dist+grid[nrow][ncol];
                        pq.push({v[nrow][ncol],{nrow,ncol}});
                    }
                }
            }
        }

        return v[n-1][m-1];
    }
};
