class Solution {
    public int minimumObstacles(int[][] arr) {

        int n = arr.length, m = arr[0].length;

        Queue< int[] > queue = new LinkedList<>();

        queue.add(new int[]{0,0});

        int[][] cnt = new int[n][m];

        for(int[] a : cnt) Arrays.fill(a,1000000);

        cnt[0][0] = 0;

        while(!queue.isEmpty()){

            int[] cur = queue.remove();

            int i = cur[0], j = cur[1];
            int add = 0;
            if(arr[i][j] == 1) add++;

            if(i + 1 < n && cnt[i + 1][j] > cnt[i][j] + add){
                cnt[i + 1][j] = cnt[i][j] + add;
                queue.add(new int[]{i + 1, j});
            }
            if(j + 1 < m && cnt[i][j + 1] > cnt[i][j] + add){
                cnt[i][j + 1] = cnt[i][j] + add;
                queue.add(new int[]{i,j + 1});
            }
            if(i - 1 >= 0 && cnt[i - 1][j] > cnt[i][j] + add){
                cnt[i - 1][j] = cnt[i][j] + add;
                queue.add(new int[]{i - 1, j});
            }
            if(j - 1>= 0 && cnt[i][j - 1] > cnt[i][j] + add){
                cnt[i][j - 1] = cnt[i][j] + add;
                queue.add(new int[]{i, j - 1});
            }
        }

        return cnt[n - 1][m -1];
    }
}
