class Solution {
public:

    int minimumObstacles(vector<vector<int>>& grid) {


        int n = grid.size();
        int m = grid[0].size();

        int dr[] = {1,0,-1,0};
        int dc[] = {0,1,0,-1};

        set<pair<int,pair<int,int>>> st;
        st.insert({0,{0,0}});
        vector<vector<int>> dist(n,vector<int>(m,INT_MAX));
        dist[0][0] = 0;
        while(!st.empty()){
            auto it = *(st.begin());
            int wt = it.first;
            int x = it.second.first;
            int y = it.second.second;
            st.erase(it);

            for(int k = 0; k < 4; k++){
                int nr = x+dr[k];
                int nc = y+dc[k];
                if(nr >= 0 and nr < n and nc >= 0 and nc < m){
                    int adjwt = grid[nr][nc];
                    if(adjwt+wt < dist[nr][nc]){
                        dist[nr][nc] = adjwt+wt;
                        st.insert({dist[nr][nc],{nr,nc}});
                    }
                }
            }
        }
        return dist[n-1][m-1];

    }
};
