        while(!q.empty()){
            int sz = q.size() ;
            for(int len = 0; len < sz; len++)
            {
                auto [x, y] = q.front() ;
                q.pop() ;
                for(int k = 0; k < 4; k++){
                    int xx = x + dirs[k][0] ;
                    int yy = y + dirs[k][1] ;
                    if(xx < 0 || yy < 0 || xx >= m || yy >= n)
                        continue ;
                    if(visited[xx][yy])
                        continue ;
                    visited[xx][yy] = true ;
                    if(grid[xx][yy] == 1)
                        ret.push_back({xx,yy}) ;
                    else{
                        if(xx == m-1 && yy == n-1){
                            ret.push_back({xx, yy}) ;
                            return ret ;
                        }
                        q.push({xx,yy}) ;    
                    }
                }
            }
        }
        return ret ;
    }
