        m = grid.size() ;
        n = grid[0].size() ;
        vector<vector<bool>>visited(m, vector<bool>(n)) ;
        queue<PII>q ;
        q.push({0,0}) ;
        visited[0][0] = true ;
        int step = 0 ;
        while(!q.empty())
        {
            int sz = q.size() ;
            for(int len = 0; len < sz; len++)
            {
                auto [x,y] = q.front() ;
                q.pop() ;
                for(int k = 0; k < 4; k++){
                    int xx = x + dirs[k][0] ;
                    int yy = y + dirs[k][1] ;
                    if(xx < 0 || yy < 0 || xx >= m || yy >= n)
                        continue ;
                    if(visited[xx][yy])
                        continue ;
                    visited[xx][yy] = true ;
                    if(grid[xx][yy] == 1)
                        q.push({xx, yy}) ;
                    else{
                        if(xx == m-1 && yy == n-1)
                            return step ;
                        auto next = gothrough(grid, visited, xx, yy) ;
                        for(int i = next.size()-1; i >=0; i--){
