class Solution {
public:

    int n, m;

    int minimumObstacles(vector<vector<int>>& grid) {
        this->n = grid.size();
        this->m = grid[0].size();
        vector<vector<int>> dir = {{1, 0}, {0, 1}, {-1, 0}, {0, -1}};
        auto comp = [&] (vector<int>& a, vector<int>& b) {
            return a[2] > b[2];
        };
        priority_queue<vector<int>, vector<vector<int>>, decltype(comp)> q(comp);
        vector<vector<int>> visited(n, vector<int>(m, n * m));
        visited[0][0] = 0;
        q.push({0, 0, 0});
        while (!q.empty()) {
            if (q.top()[0] == n - 1 && q.top()[1] == m - 1) return q.top()[2];
            vector<int> curr = q.top();
            q.pop();
            for (auto& d : dir) {
                int x = curr[0] + d[0], y = curr[1] + d[1];
                if (isbound(x, y) && curr[2] + grid[x][y] < visited[x][y]) {
                    visited[x][y] = curr[2] + grid[x][y];
                    q.push({x, y, visited[x][y]});
                }
            }
        }
        return -1;

    }

    bool isbound(int x, int y) {
        if (x < 0 || x >= n || y < 0 || y >= m) return false;
        return true;
    }

};
