        int m=grid[0].size();
        deque<node> q;
        q.push_front(node(0,0,0));
        vector<vector<int>> d(n,vector<int>(m,m*n));
        while(!q.empty()){
            int x= q.front().x;
            int y= q.front().y;
            int rv=q.front().remove;
            q.pop_front();
            int dx[]={1,-1,0,0};
            int dy[]={0,0,1,-1};

            for(int i=0;i<4;i++){
                int newX= x+dx[i];
                int newY= y+ dy[i];
                if(newX>=0 && newY >=0 && newX<n && newY <m){
                    if(grid[newX][newY] + rv < d[newX][newY]){
                        d[newX][newY] = grid[newX][newY] + rv;
                        if(grid[newX][newY]==0){
                            q.push_front(node(newX,newY,grid[newX][newY] + rv));
                        }
                        else{
                            q.push_back(node(newX,newY,grid[newX][newY] + rv));
                        }
                    }
                }

            }
        }
        return d[n-1][m-1];
    }
};
