    int kt[100001],F[100001];
priority_queue<tuple<int,int,int>,vector<tuple<int,int,int>>,greater<tuple<int,int,int>>> A; 
    void check(int x,int y,int value,vector<vector<int>>& grid)
    {
        if(x>=0&&x<=grid[0].size()-1&&y>=0&&y<=grid.size()-1&&kt[y*grid[0].size()+x]==0&&F[y*grid[0].size()+x]>value+grid[y][x])
        {
            F[y*grid[0].size()+x]=value+grid[y][x];
            A.push(make_tuple(value+grid[y][x],x,y));
        }
    }
    int minimumObstacles(vector<vector<int>>& grid) {
        int value,x,y;
        A.push(make_tuple(0,0,0));
        memset(F,127,sizeof(F));
        while(A.empty()==0)
        {
            value=get<0>(A.top());
            x=get<1>(A.top());
            y=get<2>(A.top());
            A.pop();
            if(x==grid[0].size()-1&&y==grid.size()-1) return value;
            if(kt[y*grid[0].size()+x]==0)
            {
                kt[y*grid[0].size()+x]=1;
                check(x+1,y,value,grid);
                check(x-1,y,value,grid);
                check(x,y+1,value,grid);
                check(x,y-1,value,grid);
            }   
        }
        return 0;
