        pq.push({0, {0, 0}});
        vector<vector<bool>> vis(m, vector<bool> (n, false));
        vis[0][0] = true;
        while(!pq.empty()){
            auto it = pq.top();
            pq.pop();
            int cost = it.first;
            if(it.second.first == m - 1 and it.second.second == n - 1) return cost;
            for(auto dir : dirs){
                int nx = it.second.first + dir[0];
                int ny = it.second.second + dir[1];
                if(nx >= 0 and nx < m and ny >= 0 and ny < n and !vis[nx][ny]){
                    vis[nx][ny] = true;
                    pq.push({cost + grid[nx][ny], {nx, ny}});
                }
            }
        }
        return 0;
    }
