class Solution {
    int[][] dir = {{0, 1}, {1, 0}, {-1, 0}, {0, -1}};
    public int minimumObstacles(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        boolean[][] visited = new boolean[m][n];
        Queue<int[]> pq = new PriorityQueue<>((a, b) -> a[1] - b[1]);
        pq.offer(new int[] {0, grid[0][0]});
        visited[0][0] = true;

        while(!pq.isEmpty()) {
            int[] curr = pq.poll();
            int r = curr[0] / n;
            int c = curr[0] % n;
            int cost = curr[1];
            if(r == m - 1 && c == n - 1)
                return cost;

            for(int[] d : dir) {
                int row = r + d[0];
                int col = c + d[1];
                if(!valid(m, n, row, col) || visited[row][col]) 
                    continue;

                visited[row][col] = true;
                int cell = row * n + col;
                pq.offer(new int[] {cell, cost + grid[row][col]});
            }
        }

        return -1;
    }
    public boolean valid(int m, int n, int r, int c) {
        return r >= 0 && c >= 0 && r < m && c < n;
    }
}
