    int r=grid.size()-1;
    visited.insert({0,0});
    int c=grid[0].size()-1;
    vector<vector<int>> directions={{0,-1},{0,1},{-1,0},{1,0}};
    while(pq.size()>0)
    {
        pair<int,pair<int,int>> p=pq.top();
        pq.pop();
        if (p.second.first==r && p.second.second==c)
            return p.first;
        int cr=p.second.first;
