    const vector<vector<int>> dir{{1, 0}, {0, 1}, {-1, 0}, {0, -1}};
    int minimumObstacles(vector<vector<int>>& grid) {
        const int N = grid.size();
        const int M = grid[0].size();
        vector<vector<int>> count(N, vector<int>(M, INT_MAX));
        priority_queue<Data, vector<Data>, Comp> q;
        count[0][0] = 0;
        q.push(make_data(0, 0, 0));
        int curr_r, curr_c;

        while (q.empty() == false)
        {
            auto [cnt, r, c] = q.top();
            q.pop();

            for (const vector<int>& d : dir)
            {
                curr_r = r + d[0];
                curr_c = c + d[1];

                if (inRange(N, M, curr_r, curr_c) && count[curr_r][curr_c] > cnt + grid[curr_r][curr_c])
                    count[curr_r][curr_c] = cnt + grid[curr_r][curr_c], q.push(make_data(count[curr_r][curr_c], curr_r, curr_c));
            } 
        }

        return count[N - 1][M - 1];

    }

private:
    struct Data
    {
        int count;
        int row;
        int col;
        Data(const int& i, const int& j, const int& k) : count(i), row(j), col(k) {} 
    };

    Data make_data(const int& i, const int& j, const int& k) { return Data(i, j, k); }

    class Comp
    {
    public:
        inline bool operator()(const Data& d1, const Data& d2) { return d1.count >= d2.count; }
    };

    inline bool inRange(const int& N, const int& M, const int& row, const int& col) { return row >= 0 && row < N && col >= 0 && col < M; }
};
