type PriorityQueue [][3]int

func (pq PriorityQueue) Len() int           { return len(pq) }
func (pq PriorityQueue) Less(i, j int) bool { return pq[i][0] < pq[j][0] }
func (pq PriorityQueue) Swap(i, j int)      { pq[i], pq[j] = pq[j], pq[i] }

func (pq *PriorityQueue) Push(x interface{}) {
  *pq = append(*pq, x.([3]int))
}

func (pq *PriorityQueue) Pop() interface{} {
  old := *pq
  n := len(old)
  item := old[n-1]
  *pq = old[:n-1]
  return item
}

func (pq PriorityQueue) Top() interface{} {
  return pq[0]
}

func minimumObstacles(grid [][]int) int {
  m, n := len(grid), len(grid[0])
  var pq PriorityQueue
  var res int
  heap.Push(&pq, [3]int{grid[0][0], 0, 0})
  directions := [4][2]int{{0, -1}, {-1, 0}, {0, 1}, {1, 0}}
  visited := make([][]bool, m)
  for i := 0; i < len(visited); i++ {
    visited[i] = make([]bool, n)
  }
  visited[0][0] = true
  for pq.Len() > 0 {
    top := heap.Pop(&pq).([3]int)
    x, y := top[1], top[2]
    if x == m-1 && y == n-1 {
      res = top[0]
      break
    }
    for _, dir := range directions {
      x1, y1 := x + dir[0], y + dir[1]
      if x1 >= 0 && x1 < m && y1 >= 0 && y1 < n && !visited[x1][y1] {
        visited[x1][y1] = true
        heap.Push(&pq, [3]int{top[0] + grid[x1][y1], x1, y1})
      }
    }
  }
  return res
}
