class Solution {
public:
    int minimumObstacles(vector<vector<int>>& grid) {
        priority_queue< pair<int,pair<int,int>>, vector<pair<int,pair<int,int>>> ,greater<pair<int,pair<int,int>>> >pq;
        int n=grid.size();
        int m=grid[0].size();
        vector<vector<int>>vis(n,vector<int>(m,0));
        pq.push({0,{0,0}});
        vis[0][0]=1;
        int nrow[]={-1,0,+1,0};
        int ncol[]={0,+1,0,-1};
        while(!pq.empty())
        {
            int x=pq.top().second.first;
            int y=pq.top().second.second;
            int obstacle=pq.top().first;
            pq.pop();
            if(x==n-1 && y==m-1)
            {
                return obstacle;
            }
            for(int i=0;i<4;i++)
            {
                int xnode=x+nrow[i];
                int ynode=y+ncol[i];
                if(xnode>=0 && xnode<n && ynode>=0 && ynode<m && vis[xnode][ynode]==0)
                {
                    if(grid[xnode][ynode]==0)
                    {
                        pq.push({obstacle,{xnode,ynode}});
                    }
                    else
                    {
                        pq.push({obstacle+1,{xnode,ynode}});
                    }
                    vis[xnode][ynode]=1;
                }
            }

        }
        return 0;
    }
};
