class Solution {
    public int minimumObstacles(int[][] grid) {
        int m=grid.length;
        int n=grid[0].length;
        boolean vis[][]=new boolean[m][n];

        // max priority queue
        PriorityQueue<int[]> pq=new PriorityQueue<>((a,b)->a[0]-b[0]);
        if(grid[0][0]==0)
        pq.add(new int[]{0,0,0});
        else
        pq.add(new int[]{1,0,0});

        vis[0][0]=true;
        int[][] directions = new int[][] {{-1, 0}, {0, -1}, {1, 0}, {0, 1}};

        while(!pq.isEmpty()){
            int current[]=pq.poll();
            // if reached to last index
            if(current[1]==m-1 && current[2]==n-1)
            return current[0];

            for(int dir[] :directions){
                int delrow=current[1]+dir[0];
                int delcol=current[2]+dir[1];

                if(delrow<0 || delcol<0 || delrow==m ||delcol==n ||vis[delrow][delcol])
                continue;
                vis[delrow][delcol]=true;

                if(grid[delrow][delcol]==1)
                pq.add(new int[] {current[0]+1,delrow,delcol});
                else
                pq.add(new int[]{current[0],delrow,delcol});
            }
        }
        return -1;
    }
}
