import java.util.*;
 class Pair{
    int a ;
    int b;
    int c;
    Pair(int a ,int b){
        this.a = a;
        this.b= b;
    }
    Pair(int a,int b ,int c){
        this.a= a;
        this.b= b;
        this.c= c;
    }
}
class Solution {
    public int minimumObstacles(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int[][] distance = new int[n][m];
        for(int i=0;i<n;i++){
          for(int j=0;j<m;j++){
              distance[i][j]= Integer.MAX_VALUE;
          }
        }
    Deque<Pair> dequeue = new LinkedList<>();
    dequeue.add(new Pair(0,0,0));
    ArrayList<Pair> list = new ArrayList<>();
    list.add(new Pair(1,0));
    list.add(new Pair(-1,0));
    list.add(new Pair(0,1));
    list.add(new Pair(0,-1));
    distance[0][0]=0;
    while(!dequeue.isEmpty()){
        int x = dequeue.peekFirst().a;
        int y = dequeue.peekFirst().b;
        int obs = dequeue.peekFirst().c;
        dequeue.poll();
         for(Pair i:list){
             int r = x+i.a;
             int c = y+i.b;
             if(r>=0 && r<n && c>=0 &&c<m){
             if(grid[r][c]==0){
                 if(obs<distance[r][c]){
                 distance[r][c] = obs;
                dequeue.addFirst(new Pair(r,c,distance[r][c]));
                }
             }
            else{
                if(obs+1<distance[r][c]){
                    distance[r][c] =obs+1;
                dequeue.addLast(new Pair(r,c,distance[r][c]));
                }
            }
            }
            }
         }
return distance[n-1][m-1];
}
}

