    public int minimumObstacles(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        int[][] distFromStart = new int[m][n];

        for (int i = 0; i < m; i++) {
            Arrays.fill(distFromStart[i], -1);
        }

        distFromStart[0][0] = grid[0][0];
        Queue<State> pq = new PriorityQueue<>((a, b) -> a.maxFromStart - b.maxFromStart);
        pq.offer(new State(0, 0, grid[0][0]));

        while (!pq.isEmpty()) {
            //no matter which points, only check from start to which point is more close.
            State cur = pq.poll();
            int cx = cur.x, cy = cur.y, curFromStart = cur.maxFromStart;
            if (cx == m - 1 && cy == n - 1) return curFromStart;

            for (int[] adj : getAdjs(grid, cx, cy, m, n)) {
                int nx = adj[0];
                int ny = adj[1];

                int timeFromStart = distFromStart[cx][cy] + grid[nx][ny];
                //find the shorter time or deal with the initial reachable situation.
                if (timeFromStart < distFromStart[nx][ny] || distFromStart[nx][ny] == -1) {
                    distFromStart[nx][ny] = timeFromStart;
                    pq.offer(new State(nx, ny, timeFromStart));
                }
            }
        }
        return -1;
    }

    int[][] dirs = new int[][]{{0, 1}, {0, -1}, {1, 0}, {-1, 0}};

    //!!! no need to build graph, only need to return 4 directions' adj nodes.
    List<int[]> getAdjs(int[][] g, int x, int y, int m, int n) {
        List<int[]> adjs = new ArrayList<>();
        for (int[] d : dirs) {
            int nx = x + d[0];
            int ny = y + d[1];

            if (nx >= m || ny >= n || nx < 0 || ny < 0) {
                continue;
            }
            adjs.add(new int[]{nx, ny});
        }
        return adjs;
    }


    class State {
        int x, y;
        int maxFromStart;

        State(int x, int y, int maxFromStart) {
            this.x = x;
            this.y = y;
            this.maxFromStart = maxFromStart;
        }
    }
