class Solution {
    public int minimumObstacles(int[][] grid) {


        int r =  grid.length;
        int c = grid[0].length;

        boolean [][] visited = new boolean[r][c];

        //keep the min obstracle priority queue
        Queue< int [] > q = new PriorityQueue<>((a,b) -> a[2] - b[2]);

        //priority queue contains i, j, and minimul obstracle till i, j

        q.add(new int[]{0,0,grid[0][0]});

        int [][] dir = {{1,0},{-1,0}, {0, 1},{0, -1}};

        visited[0][0] = true;

        while(!q.isEmpty()){

            int [] p = q.poll();


            //reached destination return min number of obstracle 
            if(p[0] == r -1 && p[1] == c-1){
                return p[2];
            }

            for( int [] d : dir){

                int newi = d[0] + p[0];
                int newj = d[1] + p[1];

                if(newi < 0 || newi > r-1 || newj < 0 || newj > c-1 || visited[newi][newj]) continue;

                q.add(new int[]{newi,newj,p[2] + grid[newi][newj]});

                visited[newi][newj] = true;

            }

        }
        return 0;
    }
