      const int groupSz = firstChar2Suffix.size();
      keys.reserve(groupSz);
      for (const auto& p : firstChar2Suffix) {
          keys.push_back(p.first);
      }
      long long res = 0;
      for (int i = 0; i < groupSz; ++i) {
